toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(StochasticTwentyNineCoating.meshes.length*2 + 3);

slicer.setSliceHeight(StochasticTwentyNineCoating.sliceHeight);
pather.set("PathWidth", StochasticTwentyNineCoating.pathWidth);
for (var i = 0; i < StochasticTwentyNineCoating.meshes.length; ++i) {
  progress.log("Slicing StochasticTwentyNineCoating Mesh");
  slicer.doSlicing(StochasticTwentyNineCoating.meshes[i]);
  progress.step();
  progress.log("Pathing StochasticTwentyNineCoating Mesh");
  pather.doPathing(StochasticTwentyNineCoating.meshes[i]);
  progress.step();
}

var StochasticTwentyNineCoatingMaterialCalibration = makeCalib(StochasticTwentyNineCoating);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("StochasticTwentyNineCoating", StochasticTwentyNineCoatingMaterialCalibration, StochasticTwentyNineCoating.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
