toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.log("START");
progress.setSteps(StochasticTwentyNine.meshes.length*2 + 3);
progress.log("Set STEPS");
slicer.setSliceHeight(StochasticTwentyNine.sliceHeight);
pather.set("PathWidth", StochasticTwentyNine.pathWidth);
progress.log("SET VARIABLES");

for (var i = 0; i < StochasticTwentyNine.meshes.length; ++i) {
  progress.log("Slicing StochasticTwentyNine Mesh");
  slicer.doSlicing(StochasticTwentyNine.meshes[i]);
  progress.step();
  progress.log("Pathing StochasticTwentyNine Mesh");
  pather.doPathing(StochasticTwentyNine.meshes[i]);
  progress.step();
}

var StochasticTwentyNineMaterialCalibration = makeCalib(StochasticTwentyNine);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("StochasticTwentyNine", StochasticTwentyNineMaterialCalibration, StochasticTwentyNine.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
