toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(HardStochastic.meshes.length*2 + 3);

slicer.setSliceHeight(HardStochastic.sliceHeight);
pather.set("PathWidth", HardStochastic.pathWidth);
for (var i = 0; i < HardStochastic.meshes.length; ++i) {
  progress.log("Slicing HardStochastic Mesh");
  slicer.doSlicing(HardStochastic.meshes[i]);
  progress.step();
  progress.log("Pathing HardStochastic Mesh");
  pather.doPathing(HardStochastic.meshes[i]);
  progress.step();
}

var HardStochasticMaterialCalibration = makeCalib(HardStochastic);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("HardStochastic", HardStochasticMaterialCalibration, HardStochastic.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
