toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(MediumStochastic2.meshes.length*2 + 3);

slicer.setSliceHeight(MediumStochastic2.sliceHeight);
pather.set("PathWidth", MediumStochastic2.pathWidth);
for (var i = 0; i < MediumStochastic2.meshes.length; ++i) {
  progress.log("Slicing MediumStochastic2 Mesh");
  slicer.doSlicing(MediumStochastic2.meshes[i]);
  progress.step();
  progress.log("Pathing MediumStochastic2 Mesh");
  pather.doPathing(MediumStochastic2.meshes[i]);
  progress.step();
}

var MediumStochastic2MaterialCalibration = makeCalib(MediumStochastic2);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("MediumStochastic2", MediumStochastic2MaterialCalibration, MediumStochastic2.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
