toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = 0*(x.pushout+x.suckback)*.5;
  x.AreaConstant  = 1;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}


progress.setSteps(PLA2.meshes.length*2 + 4);

slicer.setSliceHeight(PLA2.sliceHeight);
pather.set("PathWidth", PLA2.pathWidth);
for (var i = 0; i < PLA2.meshes.length; ++i) {
  progress.log("Slicing PLA2 Mesh");
  slicer.doSlicing(PLA2.meshes[i]);
  progress.step();
  progress.log("Pathing PLA2 Mesh");
  pather.doPathing(PLA2.meshes[i]);
  progress.step();
}

var PLA2MaterialCalibration = makeCalib(PLA2);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("PLA2", PLA2MaterialCalibration, PLA2.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
