toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(Stochastic60.meshes.length*2 + 3);

slicer.setSliceHeight(Stochastic60.sliceHeight);
pather.set("PathWidth", Stochastic60.pathWidth);
for (var i = 0; i < Stochastic60.meshes.length; ++i) {
  progress.log("Slicing Stochastic60 Mesh");
  slicer.doSlicing(Stochastic60.meshes[i]);
  progress.step();
  progress.log("Pathing Stochastic60 Mesh");
  pather.doPathing(Stochastic60.meshes[i]);
  progress.step();
}

var Stochastic60MaterialCalibration = makeCalib(Stochastic60);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("Stochastic60", Stochastic60MaterialCalibration, Stochastic60.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
