toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(Stochastic71.meshes.length*2 + 3);

slicer.setSliceHeight(Stochastic71.sliceHeight);
pather.set("PathWidth", Stochastic71.pathWidth);
for (var i = 0; i < Stochastic71.meshes.length; ++i) {
  progress.log("Slicing Stochastic71 Mesh");
  slicer.doSlicing(Stochastic71.meshes[i]);
  progress.step();
  progress.log("Pathing Stochastic71 Mesh");
  pather.doPathing(Stochastic71.meshes[i]);
  progress.step();
}

var Stochastic71MaterialCalibration = makeCalib(Stochastic71);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("Stochastic71", Stochastic71MaterialCalibration, Stochastic71.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
